INSERT INTO `node_revisions` VALUES (577,577,1,'ESN Satellite Manual','<p>This manual will help you editing and configuring your ESN&nbsp;Satellite.</p>\r\n<div class=\"warning\">Do you wish to join the <strong>ESN Webteam,</strong> and help out with the ESN Satellite and other ESN web projects? You could help us out with <strong>coding</strong>, writing <strong>documentation</strong>, <strong>PR</strong>, or whatever you wish to get involved with. Drop us an email at <a href=\"mailto:webteam@esn.org?subject=I%20wish%20to%20join%20the%20ESN%20Webteam&amp;body=I%20want%20to%20help%20out%20with%20...\">webteam@esn.org</a>.</div>','<p>This manual will help you editing and configuring your ESN&nbsp;Satellite.</p>\r\n<div class=\"warning\">Do you wish to join the <strong>ESN Webteam,</strong> and help out with the ESN Satellite and other ESN web projects? You could help us out with <strong>coding</strong>, writing <strong>documentation</strong>, <strong>PR</strong>, or whatever you wish to get involved with. Drop us an email at <a href=\"mailto:webteam@esn.org?subject=I%20wish%20to%20join%20the%20ESN%20Webteam&amp;body=I%20want%20to%20help%20out%20with%20...\">webteam@esn.org</a>.</div>','',1246435878,1);
