INSERT INTO `node_revisions` VALUES (590,590,1,'Find support','<p>For all your questions regarding ESN&nbsp;Satellite, please mail the <a href=\"http://groups.google.com/group/esn-satellite\">ESN&nbsp;Satellite Google Groups</a>, which contains over 100 passionate ESN&nbsp;Satellite users and developers.</p>\r\n<div class=\"warning\">Do you wish to join the <strong>ESN Webteam,</strong> and help out with the ESN Satellite and other ESN web projects? You could help us out with <strong>coding</strong>, writing <strong>documentation</strong>, <strong>PR</strong>, or whatever you wish to get involved with. Drop us an email at <a href=\"mailto:webteam@esn.org?subject=I%20wish%20to%20join%20the%20ESN%20Webteam&amp;body=I%20want%20to%20help%20out%20with%20...\">webteam@esn.org</a>.</div>','<p>For all your questions regarding ESN&nbsp;Satellite, please mail the <a href=\"http://groups.google.com/group/esn-satellite\">ESN&nbsp;Satellite Google Groups</a>, which contains over 100 passionate ESN&nbsp;Satellite users and developers.</p>','',1246435912,1);
