INSERT INTO `node_type` VALUES ('article','Article','node','An <em>article</em> is suitable for news items and press releases. By default, an <em>article</em> entry is automatically featured on the site front page, and provides the ability to post comments.','',1,'Title',1,'Body',0,1,1,0,'story');
INSERT INTO `node_type` VALUES ('book','Book page','node','A <em>book page</em> is a page of content, organized into a collection of related entries collectively known as a <em>book</em>. A <em>book page</em> automatically displays links to adjacent pages, providing a simple navigation system for organizing and reviewing structured content.','',1,'Title',1,'Body',0,1,1,0,'book');
INSERT INTO `node_type` VALUES ('event','Event','node','An <em>event</em> is suitable for any content that is strongly associated with a date, such as a meeting or a conference. Events are displayed in a calendar accessible from the main menu.','',1,'Title',1,'Body',0,1,1,0,'');
INSERT INTO `node_type` VALUES ('image','Image','image','An image (with thumbnail). This is ideal for publishing photographs or screenshots.','',1,'Title',1,'Body',0,0,0,1,'image');
INSERT INTO `node_type` VALUES ('page','Page','node','A <em>page</em> is suitable for information that rarely changes, such as an \"About us\" section of a website or institutional information. By default, a <em>page</em> entry does not allow visitor comments.','',1,'Title',1,'Body',10,1,1,0,'page');
INSERT INTO `node_type` VALUES ('partner','Partner','node','A <em>partner</em> content type is suitable for creating and displaying partners of your project. <em>Partners</em> will be featured with their logo on the homepage, and you can give some more information and a link to their website.','',1,'Name',1,'Description',0,1,1,0,'');
